properties.query:
let q0=_Im_ProcessCreate
| where (TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains " /create " and TargetProcessCommandLine contains " /sc once " and TargetProcessCommandLine contains "\\Temp\\"))|extend RuleId="39019a4e-317f-4ce3-ae63-309a8c6b53c5";
let q1=_Im_ProcessCreate
| where (TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains "/delete" and TargetProcessCommandLine contains "/tn") and (TargetProcessCommandLine contains "\\Windows\\SystemRestore\\SR" or TargetProcessCommandLine contains "\\Windows\\Windows Defender\\" or TargetProcessCommandLine contains "\\Windows\\BitLocker" or TargetProcessCommandLine contains "\\Windows\\WindowsBackup\\" or TargetProcessCommandLine contains "\\Windows\\WindowsUpdate\\" or TargetProcessCommandLine contains "\\Windows\\UpdateOrchestrator\\" or TargetProcessCommandLine contains "\\Windows\\ExploitGuard"))|extend RuleId="dbc1f800-0fe0-4bc0-9c66-292c2abe3f78";
let q2=_Im_ProcessCreate
| where (TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains " /delete " and TargetProcessCommandLine contains "/tn *" and TargetProcessCommandLine contains " /f"))|extend RuleId="220457c1-1c9f-4c2e-afe6-9598926222c1";
let q3=_Im_ProcessCreate
| where (TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains "/Change" and TargetProcessCommandLine contains "/TN" and TargetProcessCommandLine contains "/disable") and (TargetProcessCommandLine contains "\\Windows\\SystemRestore\\SR" or TargetProcessCommandLine contains "\\Windows\\Windows Defender\\" or TargetProcessCommandLine contains "\\Windows\\BitLocker" or TargetProcessCommandLine contains "\\Windows\\WindowsBackup\\" or TargetProcessCommandLine contains "\\Windows\\WindowsUpdate\\" or TargetProcessCommandLine contains "\\Windows\\UpdateOrchestrator\\" or TargetProcessCommandLine contains "\\Windows\\ExploitGuard"))|extend RuleId="9ac94dc8-9042-493c-ba45-3b5e7c86b980";
let q4=_Im_ProcessCreate
| where ((((TargetProcessName endswith "\\schtasks.exe" and TargetProcessCommandLine contains " /create ") and (TargetProcessCommandLine contains "%AppData%" or TargetProcessCommandLine contains "\\AppData\\Local\\" or TargetProcessCommandLine contains "\\AppData\\Roaming\\" or TargetProcessCommandLine contains "%Public%" or TargetProcessCommandLine contains "\\Users\\Public" or TargetProcessCommandLine contains "C:\\Windows\\Temp" or TargetProcessCommandLine contains "C:\\Perflogs")) or (ActingProcessCommandLine endswith "\\svchost.exe -k netsvcs -p -s Schedule" and (TargetProcessCommandLine contains "%Public%" or TargetProcessCommandLine contains "\\Users\\Public" or TargetProcessCommandLine contains "C:\\Windows\\Temp" or TargetProcessCommandLine contains "C:\\Perflogs"))) and (not((((TargetProcessCommandLine contains "update_task.xml" or TargetProcessCommandLine contains "/Create /TN TVInstallRestore /TR") or ActingProcessCommandLine contains "unattended.ini") or (TargetProcessCommandLine contains "/Create /Xml \"C:\\Users\\" and TargetProcessCommandLine contains "\\AppData\\Local\\Temp\\.CR." and TargetProcessCommandLine contains "Avira_Security_Installation.xml") or ((TargetProcessCommandLine contains "/Create /F /TN" and TargetProcessCommandLine contains "/Xml " and TargetProcessCommandLine contains "\\AppData\\Local\\Temp\\is-" and TargetProcessCommandLine contains "Avira_") and (TargetProcessCommandLine contains ".tmp\\UpdateFallbackTask.xml" or TargetProcessCommandLine contains ".tmp\\WatchdogServiceControlManagerTimeout.xml" or TargetProcessCommandLine contains ".tmp\\SystrayAutostart.xml" or TargetProcessCommandLine contains ".tmp\\MaintenanceTask.xml")) or (TargetProcessCommandLine contains "\\AppData\\Local\\Temp\\" and TargetProcessCommandLine contains "/Create /TN \"klcp_update\" /XML " and TargetProcessCommandLine contains "\\klcp_update_task.xml")))))|extend RuleId="81325ce1-be01-4250-944f-b4789644556f";
let q5=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\schtasks.exe" or TargetProcessFilename =~ "schtasks.exe") and TargetProcessCommandLine contains " /create " and (TargetProcessCommandLine contains "powershell" or TargetProcessCommandLine contains "pwsh" or TargetProcessCommandLine contains "cmd /c " or TargetProcessCommandLine contains "cmd /k " or TargetProcessCommandLine contains "cmd /r " or TargetProcessCommandLine contains "cmd.exe /c " or TargetProcessCommandLine contains "cmd.exe /k " or TargetProcessCommandLine contains "cmd.exe /r ") and (TargetProcessCommandLine contains "C:\\ProgramData\\" or TargetProcessCommandLine contains "%ProgramData%"))|extend RuleId="8a8379b8-780b-4dbf-b1e9-31c8d112fefb";
let q6=_Im_ProcessCreate
| where ((TargetProcessName contains "\\schtasks.exe" or TargetProcessFilename =~ "schtasks.exe") and (TargetProcessCommandLine contains "wscript" or TargetProcessCommandLine contains "vbscript" or TargetProcessCommandLine contains "cscript" or TargetProcessCommandLine contains "wmic " or TargetProcessCommandLine contains "wmic.exe" or TargetProcessCommandLine contains "regsvr32.exe" or TargetProcessCommandLine contains "powershell" or TargetProcessCommandLine contains "\\AppData\\") and (TargetProcessCommandLine contains "once" and TargetProcessCommandLine contains "00:00"))|extend RuleId="970823b7-273b-460a-8afc-3a6811998529";
let q7=_Im_ProcessCreate
| where (ActingProcessName =~ "C:\\WINDOWS\\System32\\svchost.exe" and (ActingProcessCommandLine contains "-k netsvcs" and ActingProcessCommandLine contains "-s Schedule") and (TargetProcessCommandLine endswith " -windowstyle hidden" or TargetProcessCommandLine endswith " -w hidden" or TargetProcessCommandLine endswith " -ep bypass" or TargetProcessCommandLine endswith " -noni"))|extend RuleId="b66474aa-bd92-4333-a16c-298155b120df";
let q8=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\schtasks.exe" or TargetProcessFilename =~ "schtasks.exe") and TargetProcessCommandLine contains "/Create" and (TargetProcessCommandLine contains "FromBase64String" or TargetProcessCommandLine contains "encodedcommand") and (TargetProcessCommandLine contains "Get-ItemProperty" or TargetProcessCommandLine contains " gp ") and (TargetProcessCommandLine contains "HKCU:" or TargetProcessCommandLine contains "HKLM:" or TargetProcessCommandLine contains "registry::" or TargetProcessCommandLine contains "HKEY_"))|extend RuleId="c4eeeeae-89f4-43a7-8b48-8d1bdfa66c78";
let q9=_Im_ProcessCreate
| where (((TargetProcessName endswith "\\schtasks.exe" or TargetProcessFilename =~ "schtasks.exe") and (TargetProcessCommandLine contains " ONLOGON " or TargetProcessCommandLine contains " ONSTART " or TargetProcessCommandLine contains " ONCE " or TargetProcessCommandLine contains " ONIDLE ")) and (not((TargetProcessCommandLine contains "NT AUT" or TargetProcessCommandLine contains " SYSTEM" or TargetProcessCommandLine contains "HIGHEST"))))|extend RuleId="24c8392b-aa3c-46b7-a545-43f71657fe98";
let q10=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\schtasks.exe" and TargetProcessCommandLine contains "/Create ") and (((TargetProcessCommandLine contains "/sc minute " or TargetProcessCommandLine contains "/ru system ") and (TargetProcessCommandLine contains "cmd /c" or TargetProcessCommandLine contains "cmd /k" or TargetProcessCommandLine contains "cmd /r" or TargetProcessCommandLine contains "cmd.exe /c " or TargetProcessCommandLine contains "cmd.exe /k " or TargetProcessCommandLine contains "cmd.exe /r ")) or (TargetProcessCommandLine contains " -decode " or TargetProcessCommandLine contains " -enc " or TargetProcessCommandLine contains " -w hidden " or TargetProcessCommandLine contains " bypass " or TargetProcessCommandLine contains " IEX" or TargetProcessCommandLine contains ".DownloadData" or TargetProcessCommandLine contains ".DownloadFile" or TargetProcessCommandLine contains ".DownloadString" or TargetProcessCommandLine contains "/c start /min " or TargetProcessCommandLine contains "FromBase64String" or TargetProcessCommandLine contains "mshta http" or TargetProcessCommandLine contains "mshta.exe http") or ((TargetProcessCommandLine contains "\\AppData\\" and TargetProcessCommandLine contains "%AppData%" and TargetProcessCommandLine contains "%Temp%" and TargetProcessCommandLine contains "%tmp%" and TargetProcessCommandLine contains "C:\\Windows\\Temp\\") and (TargetProcessCommandLine contains "/xml C:\\Users\\" and TargetProcessCommandLine contains "cscript" and TargetProcessCommandLine contains "curl" and TargetProcessCommandLine contains "wscript"))))|extend RuleId="f2c64357-b1d2-41b7-849f-34d2682c0fad";
let q11=_Im_ProcessCreate
| where (((TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains " /change " or TargetProcessCommandLine contains " /create ")) and TargetProcessCommandLine contains "/ru " and (TargetProcessCommandLine contains "NT AUT" or TargetProcessCommandLine contains " SYSTEM ")) and (not(((TargetProcessName endswith "\\schtasks.exe" and (TargetProcessCommandLine contains "/TN TVInstallRestore" and TargetProcessCommandLine contains "\\TeamViewer_.exe")) or (TargetProcessCommandLine contains "/Create /F /RU System /SC WEEKLY /TN AviraSystemSpeedupVerify /TR " or TargetProcessCommandLine contains ":\\Program Files (x86)\\Avira\\System Speedup\\setup\\avira_speedup_setup.exe" or TargetProcessCommandLine contains "/VERIFY /VERYSILENT /NOSTART /NODOTNET /NORESTART\" /RL HIGHEST")))))|extend RuleId="89ca78fd-b37c-4310-b3d3-81a023f83936";
let q12=_Im_ProcessCreate
| where (ActingProcessName endswith "\\scrcons.exe" and (TargetProcessName endswith "\\svchost.exe" or TargetProcessName endswith "\\dllhost.exe" or TargetProcessName endswith "\\powershell.exe" or TargetProcessName endswith "\\pwsh.exe" or TargetProcessName endswith "\\wscript.exe" or TargetProcessName endswith "\\cscript.exe" or TargetProcessName endswith "\\schtasks.exe" or TargetProcessName endswith "\\regsvr32.exe" or TargetProcessName endswith "\\mshta.exe" or TargetProcessName endswith "\\rundll32.exe" or TargetProcessName endswith "\\msiexec.exe" or TargetProcessName endswith "\\msbuild.exe"))|extend RuleId="f6d1dd2f-b8ce-40ca-bc23-062efb686b34";
union q0, q1, q2, q3, q4, q5, q6, q7, q8, q9, q10, q11, q12;
