| where TargetProcessName =~ "C:\\Windows\\System32\\wpbbin.exe"|extend RuleId="4abc0ec4-db5a-412f-9632-26659cddf145"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wpbbin_potential_persistence.yml";
