| where (((TargetProcessFilename =~ "wscript.exe" or TargetProcessFilename =~ "cscript.exe") or (TargetProcessName endswith "\\wscript.exe" or TargetProcessName endswith "\\cscript.exe")) and (TargetProcessCommandLine contains ".csv" or TargetProcessCommandLine contains ".dat" or TargetProcessCommandLine contains ".doc" or TargetProcessCommandLine contains ".gif" or TargetProcessCommandLine contains ".jpeg" or TargetProcessCommandLine contains ".jpg" or TargetProcessCommandLine contains ".png" or TargetProcessCommandLine contains ".ppt" or TargetProcessCommandLine contains ".txt" or TargetProcessCommandLine contains ".xls" or TargetProcessCommandLine contains ".xml"))|extend RuleId="99b7460d-c9f1-40d7-a316-1f36f61d52ee"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wscript_cscript_uncommon_extension_exec.yml";
let q3=_Im_ProcessCreate
| where (((TargetProcessName endswith "\\wuauclt.exe" or TargetProcessFilename =~ "wuauclt.exe") and (TargetProcessCommandLine contains "/UpdateDeploymentProvider" and TargetProcessCommandLine contains "/RunHandlerComServer" and TargetProcessCommandLine contains ".dll")) and (not((TargetProcessCommandLine contains " /ClassId " or TargetProcessCommandLine contains " wuaueng.dll "))))|extend RuleId="d7825193-b70a-48a4-b992-8b5b3015cc11"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wuauclt_execution.yml";
let q4=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\Wuauclt.exe" or TargetProcessFilename =~ "Wuauclt.exe") and TargetProcessCommandLine endswith "\\Wuauclt.exe")|extend RuleId="52d097e2-063e-4c9c-8fbb-855c8948d135"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wuauclt_no_cli_flags_execution.yml";
let q5=_Im_ProcessCreate
