properties.query:
let q0=_Im_ProcessCreate
| where (((ActingProcessName endswith "\\w3wp.exe" or ActingProcessName endswith "\\php-cgi.exe" or ActingProcessName endswith "\\nginx.exe" or ActingProcessName endswith "\\httpd.exe" or ActingProcessName endswith "\\caddy.exe" or ActingProcessName endswith "\\ws_tomcatservice.exe") or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (ActingProcessName contains "-tomcat-" or ActingProcessName contains "\\tomcat")) or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (TargetProcessCommandLine contains "catalina.jar" or TargetProcessCommandLine contains "CATALINA_HOME"))) and ((TargetProcessCommandLine contains "rundll32" and TargetProcessCommandLine contains "comsvcs") or (TargetProcessCommandLine contains " -hp" and TargetProcessCommandLine contains " a " and TargetProcessCommandLine contains " -m") or (TargetProcessCommandLine contains "net" and TargetProcessCommandLine contains " user " and TargetProcessCommandLine contains " /add") or (TargetProcessCommandLine contains "net" and TargetProcessCommandLine contains " localgroup " and TargetProcessCommandLine contains " administrators " and TargetProcessCommandLine contains "/add") or (TargetProcessName endswith "\\ntdsutil.exe" or TargetProcessName endswith "\\ldifde.exe" or TargetProcessName endswith "\\adfind.exe" or TargetProcessName endswith "\\procdump.exe" or TargetProcessName endswith "\\Nanodump.exe" or TargetProcessName endswith "\\vssadmin.exe" or TargetProcessName endswith "\\fsutil.exe") or (TargetProcessCommandLine contains " -NoP " or TargetProcessCommandLine contains " -W Hidden " or TargetProcessCommandLine contains " -decode " or TargetProcessCommandLine contains " /decode " or TargetProcessCommandLine contains "reg save " or TargetProcessCommandLine contains ".downloadstring(" or TargetProcessCommandLine contains ".downloadfile(" or TargetProcessCommandLine contains "FromBase64String" or TargetProcessCommandLine contains " /ticket:" or TargetProcessCommandLine contains " sekurlsa" or TargetProcessCommandLine contains ".dmp full" or TargetProcessCommandLine contains "process call create" or TargetProcessCommandLine contains "whoami /priv")))|extend RuleId="4ebc877f-4612-45cb-b3a5-8e3834db36c9"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_webshell_hacking.yml"|extend RuleTitle="Webshell Hacking Activity Patterns"|extend RuleDescription="Detects certain parent child patterns found in cases in which a webshell is used to perform certain credential dumping or exfiltration activities on a compromised system";
let q1=_Im_ProcessCreate
| where (((ActingProcessName endswith "\\w3wp.exe" or ActingProcessName endswith "\\php-cgi.exe" or ActingProcessName endswith "\\nginx.exe" or ActingProcessName endswith "\\httpd.exe" or ActingProcessName endswith "\\caddy.exe" or ActingProcessName endswith "\\ws_tomcatservice.exe") or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (ActingProcessName contains "-tomcat-" or ActingProcessName contains "\\tomcat")) or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (TargetProcessCommandLine contains "catalina.jar" or TargetProcessCommandLine contains "CATALINA_HOME"))) and (TargetProcessCommandLine contains "perl --help" or TargetProcessCommandLine contains "python --help" or TargetProcessCommandLine contains "python -h" or TargetProcessCommandLine contains "python3 --help" or TargetProcessCommandLine contains "python3 -h" or TargetProcessCommandLine contains "wget --help" or TargetProcessCommandLine contains "perl -h"))|extend RuleId="f64e5c19-879c-4bae-b471-6d84c8339677"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_webshell_recon_detection.yml"|extend RuleTitle="Webshell Recon Detection Via CommandLine & Processes"|extend RuleDescription="Detects processes spawned from web servers (php, tomcat, iis...etc) that perform reconnaissance looking for the existence of popular scripting tools (perl, python, wget) on the system via the help commands";
let q2=_Im_ProcessCreate
| where (((ActingProcessName endswith "\\w3wp.exe" or ActingProcessName endswith "\\php.exe" or ActingProcessName endswith "\\php-cgi.exe" or ActingProcessName endswith "\\nginx.exe" or ActingProcessName endswith "\\httpd.exe" or ActingProcessName endswith "\\caddy.exe" or ActingProcessName endswith "\\ws_TomcatService.exe" or ActingProcessName endswith "\\tomcat.exe" or ActingProcessName endswith "\\UMWorkerProcess.exe") or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (ActingProcessName contains "-tomcat-" or ActingProcessName contains "\\tomcat")) or ((ActingProcessName endswith "\\java.exe" or ActingProcessName endswith "\\javaw.exe") and (ActingProcessCommandLine contains "catalina.jar" or ActingProcessCommandLine contains "CATALINA_HOME" or ActingProcessCommandLine contains "catalina.home"))) and (TargetProcessName endswith "\\cmd.exe" or TargetProcessName endswith "\\cscript.exe" or TargetProcessName endswith "\\sh.exe" or TargetProcessName endswith "\\bash.exe" or TargetProcessName endswith "\\powershell.exe" or TargetProcessName endswith "\\powershell_ise.exe" or TargetProcessName endswith "\\pwsh.exe" or TargetProcessName endswith "\\bitsadmin.exe" or TargetProcessName endswith "\\arp.exe" or TargetProcessName endswith "\\at.exe" or TargetProcessName endswith "\\certutil.exe" or TargetProcessName endswith "\\dsget.exe" or TargetProcessName endswith "\\dsquery.exe" or TargetProcessName endswith "\\find.exe" or TargetProcessName endswith "\\findstr.exe" or TargetProcessName endswith "\\fsutil.exe" or TargetProcessName endswith "\\hostname.exe" or TargetProcessName endswith "\\ipconfig.exe" or TargetProcessName endswith "\\nbtstat.exe" or TargetProcessName endswith "\\net.exe" or TargetProcessName endswith "\\net1.exe" or TargetProcessName endswith "\\netdom.exe" or TargetProcessName endswith "\\netsh.exe" or TargetProcessName endswith "\\netstat.exe" or TargetProcessName endswith "\\nltest.exe" or TargetProcessName endswith "\\nslookup.exe" or TargetProcessName endswith "\\ntdutil.exe" or TargetProcessName endswith "\\pathping.exe" or TargetProcessName endswith "\\ping.exe" or TargetProcessName endswith "\\qprocess.exe" or TargetProcessName endswith "\\query.exe" or TargetProcessName endswith "\\qwinsta.exe" or TargetProcessName endswith "\\reg.exe" or TargetProcessName endswith "\\rundll32.exe" or TargetProcessName endswith "\\sc.exe" or TargetProcessName endswith "\\schtasks.exe" or TargetProcessName endswith "\\systeminfo.exe" or TargetProcessName endswith "\\tasklist.exe" or TargetProcessName endswith "\\tracert.exe" or TargetProcessName endswith "\\ver.exe" or TargetProcessName endswith "\\vssadmin.exe" or TargetProcessName endswith "\\wevtutil.exe" or TargetProcessName endswith "\\whoami.exe" or TargetProcessName endswith "\\wmic.exe" or TargetProcessName endswith "\\wscript.exe" or TargetProcessName endswith "\\wusa.exe") and (not((TargetProcessCommandLine endswith "Windows\\system32\\cmd.exe /c C:\\ManageEngine\\ADManager \"Plus\\ES\\bin\\elasticsearch.bat -Enode.name=RMP-NODE1 -pelasticsearch-pid.txt" or (TargetProcessCommandLine contains "sc query" and TargetProcessCommandLine contains "ADManager Plus")))))|extend RuleId="8202070f-edeb-4d31-a010-a26c72ac5600"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_webshell_spawn.yml"|extend RuleTitle="Shells Spawned by Web Servers"|extend RuleDescription="Detects web servers that spawn shell processes which could be the result of a successfully placed web shell or another attack";
let q3=_Im_ProcessCreate
| where (((TargetProcessName endswith "\\Werfault.exe" or TargetProcessFilename =~ "WerFault.exe") and ((ParentUser contains "AUTHORI" or ParentUser contains "AUTORI") and (TargetUsername contains "AUTHORI" or TargetUsername contains "AUTORI") and (TargetProcessCommandLine contains " -u -p " and TargetProcessCommandLine contains " -ip " and TargetProcessCommandLine contains " -s "))) and (not(ActingProcessName =~ "C:\\Windows\\System32\\lsass.exe")))|extend RuleId="9a4ccd1a-3526-4d99-b980-9f9c5d3a6ff3"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_werfault_lsass_shtinkering.yml"|extend RuleTitle="Potential Credential Dumping Via WER"|extend RuleDescription="Detects potential credential dumping via Windows Error Reporting LSASS Shtinkering technique which uses the Windows Error Reporting to dump lsass";
let q4=_Im_ProcessCreate
| where (ActingProcessName endswith "\\wermgr.exe" and (TargetProcessName endswith "\\cmd.exe" or TargetProcessName endswith "\\cscript.exe" or TargetProcessName endswith "\\ipconfig.exe" or TargetProcessName endswith "\\mshta.exe" or TargetProcessName endswith "\\net.exe" or TargetProcessName endswith "\\net1.exe" or TargetProcessName endswith "\\netstat.exe" or TargetProcessName endswith "\\nslookup.exe" or TargetProcessName endswith "\\powershell_ise.exe" or TargetProcessName endswith "\\powershell.exe" or TargetProcessName endswith "\\pwsh.exe" or TargetProcessName endswith "\\regsvr32.exe" or TargetProcessName endswith "\\rundll32.exe" or TargetProcessName endswith "\\systeminfo.exe" or TargetProcessName endswith "\\whoami.exe" or TargetProcessName endswith "\\wscript.exe"))|extend RuleId="396f6630-f3ac-44e3-bfc8-1b161bc00c4e"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wermgr_susp_child_process.yml"|extend RuleTitle="Suspicious Child Process Of Wermgr.EXE"|extend RuleDescription="Detects suspicious Windows Error Reporting manager (wermgr.exe) child process";
let q5=_Im_ProcessCreate
| where (TargetProcessName endswith "\\wermgr.exe" and (not((TargetProcessName startswith "C:\\Windows\\System32\\" or TargetProcessName startswith "C:\\Windows\\SysWOW64\\" or TargetProcessName startswith "C:\\Windows\\WinSxS\\"))))|extend RuleId="5394fcc7-aeb2-43b5-9a09-cac9fc5edcd5"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wermgr_susp_exec_location.yml"|extend RuleTitle="Suspicious Execution Location Of Wermgr.EXE"|extend RuleDescription="Detects suspicious Windows Error Reporting manager (wermgr.exe) execution location.";
let q6=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\wget.exe" or TargetProcessFilename =~ "wget.exe") and TargetProcessCommandLine matches regex "://[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}" and TargetProcessCommandLine contains "http" and (TargetProcessCommandLine matches regex "\\s-O\\s" or TargetProcessCommandLine contains "--output-document") and (TargetProcessCommandLine endswith ".ps1" or TargetProcessCommandLine endswith ".ps1'" or TargetProcessCommandLine endswith ".ps1\"" or TargetProcessCommandLine endswith ".dat" or TargetProcessCommandLine endswith ".dat'" or TargetProcessCommandLine endswith ".dat\"" or TargetProcessCommandLine endswith ".msi" or TargetProcessCommandLine endswith ".msi'" or TargetProcessCommandLine endswith ".msi\"" or TargetProcessCommandLine endswith ".bat" or TargetProcessCommandLine endswith ".bat'" or TargetProcessCommandLine endswith ".bat\"" or TargetProcessCommandLine endswith ".exe" or TargetProcessCommandLine endswith ".exe'" or TargetProcessCommandLine endswith ".exe\"" or TargetProcessCommandLine endswith ".vbs" or TargetProcessCommandLine endswith ".vbs'" or TargetProcessCommandLine endswith ".vbs\"" or TargetProcessCommandLine endswith ".vbe" or TargetProcessCommandLine endswith ".vbe'" or TargetProcessCommandLine endswith ".vbe\"" or TargetProcessCommandLine endswith ".hta" or TargetProcessCommandLine endswith ".hta'" or TargetProcessCommandLine endswith ".hta\"" or TargetProcessCommandLine endswith ".dll" or TargetProcessCommandLine endswith ".dll'" or TargetProcessCommandLine endswith ".dll\"" or TargetProcessCommandLine endswith ".psm1" or TargetProcessCommandLine endswith ".psm1'" or TargetProcessCommandLine endswith ".psm1\""))|extend RuleId="17f0c0a8-8bd5-4ee0-8c5f-a342c0199f35"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wget_download_direct_ip.yml"|extend RuleTitle="Suspicious File Download From IP Via Wget.EXE"|extend RuleDescription="Detects potentially suspicious file downloads directly from IP addresses using Wget.exe";
union q0, q1, q2, q3, q4, q5, q6;
