properties.query:
let q0=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\svchost.exe" or TargetProcessName endswith "\\taskhost.exe" or TargetProcessName endswith "\\lsm.exe" or TargetProcessName endswith "\\lsass.exe" or TargetProcessName endswith "\\services.exe" or TargetProcessName endswith "\\lsaiso.exe" or TargetProcessName endswith "\\csrss.exe" or TargetProcessName endswith "\\wininit.exe" or TargetProcessName endswith "\\winlogon.exe") and (not((((ActingProcessName endswith "\\SavService.exe" or ActingProcessName endswith "\\ngen.exe") or (ActingProcessName contains "\\System32\\" or ActingProcessName contains "\\SysWOW64\\")) or ((ActingProcessName contains "\\Windows Defender\\" or ActingProcessName contains "\\Microsoft Security Client\\") and ActingProcessName endswith "\\MsMpEng.exe") or (isnull(ActingProcessName) or ActingProcessName =~ "-")))))|extend RuleId="96036718-71cc-4027-a538-d1587e0006a7"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_proc_wrong_parent.yml"|extend RuleTitle="Windows Processes Suspicious Parent Directory"|extend RuleDescription="Detect suspicious parent processes of well-known Windows processes";
let q1=_Im_ProcessCreate
| where (TargetProcessName endswith "\\te.exe" or ActingProcessName endswith "\\te.exe" or TargetProcessFilename =~ "\\te.exe")|extend RuleId="634b00d5-ccc3-4a06-ae3b-0ec8444dd51b"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_use_of_te_bin.yml"|extend RuleTitle="Malicious Windows Script Components File Execution by TAEF Detection"|extend RuleDescription="Windows Test Authoring and Execution Framework (TAEF) framework allows you to run automation by executing tests files written on different languages (C, C#, Microsoft COM Scripting interfaces
Adversaries may execute malicious code (such as WSC file with VBScript, dll and so on) directly by running te.exe
";
let q2=_Im_ProcessCreate
| where (TargetProcessCommandLine contains " -accepteula" or TargetProcessCommandLine contains " /accepteula")|extend RuleId="7cccd811-7ae9-4ebe-9afd-cb5c406b824b"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_sysinternals_eula_accepted.yml"|extend RuleTitle="Potential Execution of Sysinternals Tools"|extend RuleDescription="Detects command lines that contain the 'accepteula' flag which could be a sign of execution of one of the Sysinternals tools";
let q3=_Im_ProcessCreate
| where (TargetProcessName endswith "\\systeminfo.exe" or TargetProcessFilename =~ "sysinfo.exe")|extend RuleId="0ef56343-059e-4cb6-adc1-4c3c967c5e46"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_systeminfo_execution.yml"|extend RuleTitle="Suspicious Execution of Systeminfo"|extend RuleDescription="Detects usage of the "systeminfo" command to retrieve information";
let q4=_Im_ProcessCreate
| where (ActingProcessName endswith "\\taskmgr.exe" and (not((TargetProcessName endswith "\\resmon.exe" or TargetProcessName endswith "\\mmc.exe" or TargetProcessName endswith "\\taskmgr.exe"))))|extend RuleId="3d7679bd-0c00-440c-97b0-3f204273e6c7"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_taskmgr_susp_child_process.yml"|extend RuleTitle="Taskmgr as Parent"|extend RuleDescription="Detects the creation of a process from Windows task manager";
let q5=_Im_ProcessCreate
| where ((TargetProcessCommandLine contains "VBoxRT.dll,RTR3Init" or TargetProcessCommandLine contains "VBoxC.dll" or TargetProcessCommandLine contains "VBoxDrv.sys") or (TargetProcessCommandLine contains "startvm" or TargetProcessCommandLine contains "controlvm"))|extend RuleId="bab049ca-7471-4828-9024-38279a4c04da"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_virtualbox_execution.yml"|extend RuleTitle="Detect Virtualbox Driver Installation OR Starting Of VMs"|extend RuleDescription="Adversaries can carry out malicious operations using a virtual instance to avoid detection. This rule is built to detect the registration of the Virtualbox driver or start of a Virtualbox VM.";
let q6=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\where.exe" or TargetProcessFilename =~ "where.exe") and (TargetProcessCommandLine contains "places.sqlite" or TargetProcessCommandLine contains "cookies.sqlite" or TargetProcessCommandLine contains "formhistory.sqlite" or TargetProcessCommandLine contains "logins.json" or TargetProcessCommandLine contains "key4.db" or TargetProcessCommandLine contains "key3.db" or TargetProcessCommandLine contains "sessionstore.jsonlz4" or TargetProcessCommandLine contains "History" or TargetProcessCommandLine contains "Bookmarks" or TargetProcessCommandLine contains "Cookies" or TargetProcessCommandLine contains "Login Data"))|extend RuleId="725a9768-0f5e-4cb3-aec2-bc5719c6831a"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_where_browser_data_recon.yml"|extend RuleTitle="Suspicious Where Execution"|extend RuleDescription="Adversaries may enumerate browser bookmarks to learn more about compromised hosts.
Browser bookmarks may reveal personal information about users (ex: banking sites, interests, social media, etc.) as well as details about
internal network resources such as servers, tools/dashboards, or other related infrastructure.
";
let q7=_Im_ProcessCreate
| where ((TargetProcessName endswith "\\wmic.exe" or TargetProcessFilename =~ "wmic.exe") and TargetProcessCommandLine contains " group")|extend RuleId="164eda96-11b2-430b-85ff-6a265c15bf32"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_wmic_recon_group.yml"|extend RuleTitle="Local Groups Reconnaissance Via Wmic.EXE"|extend RuleDescription="Detects the execution of "wmic" with the "group" flag.
Adversaries may attempt to find local system groups and permission settings.
The knowledge of local system permission groups can help adversaries determine which groups exist and which users belong to a particular group.
Adversaries may use this information to determine which users have elevated permissions, such as the users found within the local administrators group.
";
union q0, q1, q2, q3, q4, q5, q6, q7;
