properties.query:
let q0=_Im_ProcessCreate
| where (TargetProcessCommandLine contains ":3389" and (TargetProcessCommandLine contains " -L " or TargetProcessCommandLine contains " -P " or TargetProcessCommandLine contains " -R " or TargetProcessCommandLine contains " -pw " or TargetProcessCommandLine contains " -ssh "))|extend RuleId="5163A000-5160-5160-5160-5163A0000116"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_remote_desktop_tunneling.yml"|extend RuleTitle="Potential Remote Desktop Tunneling"|extend RuleDescription="Detects potential use of an SSH utility to establish RDP over a reverse SSH Tunnel. This can be used by attackers to enable routing of network packets that would otherwise not reach their intended destination.";
let q1=_Im_ProcessCreate
| where (((TargetProcessName endswith "\\powershell.exe" or TargetProcessName endswith "\\pwsh.exe" or TargetProcessName endswith "\\wmic.exe" or TargetProcessName endswith "\\vssadmin.exe") or (TargetProcessFilename =~ "PowerShell.EXE" or TargetProcessFilename =~ "pwsh.dll" or TargetProcessFilename =~ "wmic.exe" or TargetProcessFilename =~ "VSSADMIN.EXE")) and (TargetProcessCommandLine contains "shadow" and TargetProcessCommandLine contains "create"))|extend RuleId="b17ea6f7-6e90-447e-a799-e6c0a493d6ce"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_shadow_copies_creation.yml";
let q2=_Im_ProcessCreate
| where TargetProcessCommandLine startswith "C:\\Windows\\Sysnative\\"|extend RuleId="3c1b5fb0-c72f-45ba-abd1-4d4c353144ab"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_sysnative.yml";
