| where (TargetProcessCommandLine contains "\\SYSVOL\\" and TargetProcessCommandLine contains "\\policies\\")|extend RuleId="05f3c945-dcc8-4393-9f3d-af65077a8f86"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_sysvol_access.yml";
let q4=_Im_ProcessCreate
| where (ActingProcessName endswith "\\userinit.exe" and (not((TargetProcessCommandLine contains "\\netlogon\\" or (TargetProcessName endswith "\\explorer.exe" or TargetProcessFilename =~ "explorer.exe")))))|extend RuleId="b655a06a-31c0-477a-95c2-3726b83d649d"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_userinit_child.yml";
let q5=_Im_ProcessCreate
| where (ActingProcessName endswith "\\vsjitdebugger.exe" and (not((TargetProcessName contains "*\\vsimmersiveactivatehelper*.exe" or TargetProcessName endswith "\\devenv.exe"))))|extend RuleId="15c7904e-6ad1-4a45-9b46-5fb25df37fd2"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_use_of_vsjitdebugger_bin.yml";
let q6=_Im_ProcessCreate
| where (TargetProcessCommandLine contains "Asd123.aaaa" or TargetProcessCommandLine contains "password123" or TargetProcessCommandLine contains "123456789" or TargetProcessCommandLine contains "P@ssw0rd!")|extend RuleId="91edcfb1-2529-4ac2-9ecc-7617f895c7e4"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_weak_or_abused_passwords.yml";
