| where (TargetProcessCommandLine contains "[System.Net.WebRequest]::create" or TargetProcessCommandLine contains "curl " or TargetProcessCommandLine contains "Invoke-RestMethod" or TargetProcessCommandLine contains "Invoke-WebRequest" or TargetProcessCommandLine contains "iwr " or TargetProcessCommandLine contains "Net.WebClient" or TargetProcessCommandLine contains "Resume-BitsTransfer" or TargetProcessCommandLine contains "Start-BitsTransfer" or TargetProcessCommandLine contains "wget " or TargetProcessCommandLine contains "WinHttp.WinHttpRequest")|extend RuleId="9fc51a3c-81b3-4fa7-b35f-7c02cf10fd2d"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_susp_web_request_cmd_and_cmdlets.yml";
