let q13=_Im_ProcessCreate
| where (TargetProcessName =~ "C:\\Windows\\PSEXESVC.exe" or TargetProcessFilename =~ "psexesvc.exe")|extend RuleId="fdfcbd78-48f1-4a4b-90ac-d82241e368c5"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_sysinternals_psexesvc.yml";
