| where ((TargetProcessFilename =~ "psloglist.exe" or (TargetProcessName endswith "\\psloglist.exe" or TargetProcessName endswith "\\psloglist64.exe")) and (TargetProcessCommandLine contains " security" or TargetProcessCommandLine contains " application" or TargetProcessCommandLine contains " system") and (TargetProcessCommandLine contains " -d" or TargetProcessCommandLine contains " /d" or TargetProcessCommandLine contains " -x" or TargetProcessCommandLine contains " /x" or TargetProcessCommandLine contains " -s" or TargetProcessCommandLine contains " /s" or TargetProcessCommandLine contains " -c" or TargetProcessCommandLine contains " /c" or TargetProcessCommandLine contains " -g" or TargetProcessCommandLine contains " /g"))|extend RuleId="aae1243f-d8af-40d8-ab20-33fc6d0c55bc"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_sysinternals_psloglist.yml";
let q15=_Im_ProcessCreate
