| where (((TargetProcessName endswith "\\Sysmon64.exe" or TargetProcessName endswith "\\Sysmon.exe") or TargetProcessFileDescription =~ "System activity monitor") and (TargetProcessCommandLine contains "-c" or TargetProcessCommandLine contains "/c"))|extend RuleId="87911521-7098-470b-a459-9a57fc80bdfd"|extend SourceURL="https://github.com/SigmaHQ/sigma/blob/master/rules/windows/process_creation/proc_creation_win_sysinternals_sysmon_config_update.yml";
